<template>
    <div class="sk-map fill-height" id="sk-map"></div>
</template>
<script>
    /* global mapboxgl*/
    //import MapBoxGL from 'mapbox-gl';
    
    export default {
        name: 'SkMap',
        props: {
            center: {
                type: Object,
                required: true
            }
        },
        data(){
            return {
                map: null
            };
        },
        created(){
            if (typeof mapboxgl === "undefined"){
                var p, s = document.createElement("link");
                s.setAttribute("rel", "stylesheet");
                s.setAttribute("href", "https://api.mapbox.com/mapbox-gl-js/v1.10.1/mapbox-gl.css");
                document.getElementsByTagName('head')[0].appendChild(s);
                s = document.createElement("script");
                s.setAttribute("src", "https://api.mapbox.com/mapbox-gl-js/v1.10.1/mapbox-gl.js");
                document.getElementsByTagName('head')[0].appendChild(s);
                s.addEventListener('load', ()=>{
                    console.log('MBX LOADED!!!');
                });
            }
        },
        mounted(){
            const _watch = ()=>{
                if ((typeof mapboxgl !== "undefined")&&( $("#sk-map").length > 0 )){
                    setTimeout(this.mapInit, 200);
                } else {
                    setTimeout(_watch, 200);
                }
            };
            _watch();
        },
        beforeDestroy(){
            if (!!this.map){
                this.map.remove();
            }
        },
        methods: {
            mapInit(){
                mapboxgl.accessToken = 'pk.eyJ1IjoidGhlbGl4IiwiYSI6ImNrYXRoNWc3MjB0ZnkzN29jcHkydjd1NmUifQ.5y-ik3c3U5eGPV_mW7zxaw';
                var coords = [this.center.lon, this.center.lat];
                var map = new mapboxgl.Map({
                    container: $("#sk-map").get(0),
                    style: 'mapbox://styles/thelix/ckathyj992krv1ins0b3ucdrm',
                    center: coords,
                    zoom: 13,
                    maxZoom: 16,
                    pitchWithRotate: false
                });
                $('.mapboxgl-ctrl-bottom-left').css({display:'none'});
                map.on('load', ()=>{
                    this.$emit('map-avail');
                    var pointImage = new Image();
                    pointImage.src = 'data:image/png;base64,';
                    pointImage.onload = function(){
                        map.addImage('pointer', pointImage);
                    };
                    map.addSource('points', {
                        'type': 'geojson',
                        'data': {
                            'type': 'FeatureCollection',
                            'features': [{
                                    'type': 'Feature',
                                    'geometry': {
                                        'type': 'Point',
                                        'coordinates': coords
                                    }
                                }]
                    }});    //map.addSource('point'...
                
                    map.addLayer({
                        'id': 'pointer',
                        'type': 'symbol',
                        'source': 'points',
                        'layout': {
                            'icon-image': 'pointer',
                            'icon-size': 0.25
                        } 
                    });
                }); //map.on('load'...
                
                this.map = map;
            }
        }
    }
</script>

<style lang="scss" scoped>
    .sk-map{
        width: 100%;
        min-height: 280px;
        background: #fff;
    }
</style>
    